import React, { Component } from 'react'
import '../css/Sidebar.scss';
import {Button} from 'reactstrap';
// import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import { Accordion, Icon } from 'semantic-ui-react'
// get our fontawesome imports
// import { faHome } from "@fortawesome/fontawesome-free";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const navLinks = [
    { url: '/about-us', name: 'About Us' },
    { url: '/projects', name: 'Projects' },
    { url: '/services', name: 'Services' },
    { url: '/contact-us', name: 'Contact Us' },
  ];


class Sidebar extends Component {


    // state = { activeIndex: 0 }


        constructor(){
          super();
          this.state = {
            style:"menu",
            menuStatus:"open",
            dropdownOpen:"false",
            setDropdownOpen:"false",
            activeIndex: 0
          };
          this.openMenu = this.openMenu.bind(this);
          this.closeMenu = this.closeMenu.bind(this);
          this.toggle=this.toggle.bind(this);
        //   this.setDropdownOpen=this.setDropdownOpen.bind(this);
        //   this.handleClick = this.handleClick.bind(this);
        };

        handleClick = (e, titleProps) => {
            const { index } = titleProps
            const { activeIndex } = this.state
            const newIndex = activeIndex === index ? -1 : index

            this.setState({ activeIndex: newIndex })
        }

        openMenu(){
            this.setState({
            menuStatus:"close",
            style:"menu active"
            });
        }

        closeMenu(){
            this.setState({
            menuStatus:"open",
            style:"menu"
            });
        }
        toggle(event) {

            this.setState({
                dropdownOpen: !this.state.dropdownOpen
            });
        }


        // handleClick() {
        //   switch(this.state.menuStatus)
        //   {
        //     case "open":
        //       this.setState({
        //         menuStatus:"close",
        //         style:"menu active"
        //       });
        //       break;
        //     case "close":
        //       this.setState({
        //         menuStatus:"open",
        //         style:"menu"
        //       });
        //       break;
        //   }        
        // }

        render() {
            const { activeIndex } = this.state
            // const toggle = () => this.setDropdownOpen(prevState => !prevState);
          return (      
            <div>
                {/* <button onClick={this.handleClick}><div></div><div></div><div></div></button> */}
              <button onClick={this.openMenu}><div></div><div></div><div></div></button>

              <div className={this.state.style}>               

                    <button className = 'close' onClick={this.closeMenu}><Button close /></button>
                  <ul>
                  {navLinks.map(({ url, name }) => (
                    <li>
                      <a href={url}>{name}</a>
                      {/* { <a href={url}>Home</a>  */}
                    </li>
                  ))} 

            //       <li>

            //         {/* <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
            //             <DropdownToggle caret>
            //                 Dropdown
            //                 </DropdownToggle>
            //             <DropdownMenu>
            //                 <DropdownItem header>Header</DropdownItem>
            //                 <DropdownItem>Some Action</DropdownItem>
            //                 <DropdownItem disabled>Action (disabled)</DropdownItem>
            //                 <DropdownItem divider />
            //                 <DropdownItem>Foo Action</DropdownItem>
            //                 <DropdownItem>Bar Action</DropdownItem>
            //                 <DropdownItem>Quo Action</DropdownItem>
            //             </DropdownMenu>
            //         </Dropdown>
            //       </li>
            //       <li>
            //         <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
            //             <DropdownToggle caret>
            //                 Dropdown
            //                 </DropdownToggle>
            //             <DropdownMenu>
            //                 <DropdownItem header>Header</DropdownItem>
            //                 <DropdownItem>Some Action</DropdownItem>
            //                 <DropdownItem disabled>Action (disabled)</DropdownItem>
            //                 <DropdownItem divider />
            //                 <DropdownItem>Foo Action</DropdownItem>
            //                 <DropdownItem>Bar Action</DropdownItem>
            //                 <DropdownItem>Quo Action</DropdownItem>
            //             </DropdownMenu>
            //         </Dropdown> */}
                </li>
              </ul>
            </div>
            <Accordion>
                        <Accordion.Title
                        active={activeIndex === 0}
                        index={0}
                        onClick={this.handleClick}
                        >
                        <Icon name='dropdown' />
                        What is a dog?
                        </Accordion.Title>
                        <Accordion.Content active={activeIndex === 0}>
                        <p>
                            A dog is a type of domesticated animal. Known for its loyalty and
                            faithfulness, it can be found as a welcome guest in many households
                            across the world.
                        </p>
                        </Accordion.Content>

                        <Accordion.Title
                        active={activeIndex === 1}
                        index={1}
                        onClick={this.handleClick}
                        >
                        <Icon name='dropdown' />
                        What kinds of dogs are there?
                        </Accordion.Title>
                        <Accordion.Content active={activeIndex === 1}>
                        <p>
                            There are many breeds of dogs. Each breed varies in size and
                            temperament. Owners often select a breed of dog that they find to be
                            compatible with their own lifestyle and desires from a companion.
                        </p>
                        </Accordion.Content>

                        <Accordion.Title
                        active={activeIndex === 2}
                        index={2}
                        onClick={this.handleClick}
                        >
                        <Icon name='dropdown' />
                        How do you acquire a dog?
                        </Accordion.Title>
                        <Accordion.Content active={activeIndex === 2}>
                        <p>
                            Three common ways for a prospective owner to acquire a dog is from
                            pet shops, private owners, or shelters.
                        </p>
                        <p>
                            A pet shop may be the most convenient way to buy a dog. Buying a dog
                            from a private owner allows you to assess the pedigree and
                            upbringing of your dog before choosing to take it home. Lastly,
                            finding your dog from a shelter, helps give a good home to a dog who
                            may not find one so readily.
                        </p>
                        </Accordion.Content>
                    </Accordion>
            </div>
          );
        }



}
