import React, { Component } from 'react'
import '../css/Sidebar.scss';
import {Button} from 'reactstrap';
import Accordion from 'react-bootstrap/Accordion'
// import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
// get our fontawesome imports
// import { faHome } from "@fortawesome/fontawesome-free";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const navLinks = [
    { url: '/about-us', name: 'About Us' },
    { url: '/projects', name: 'Projects' },
    { url: '/services', name: 'Services' },
    { url: '/contact-us', name: 'Contact Us' },
  ];


class Sidebar extends Component {


    // state = { activeIndex: 0 }


        constructor(){
          super();
          this.state = {
            style:"menu",
            menuStatus:"open",
            dropdownOpen:"false",
            setDropdownOpen:"false"
          };
          this.openMenu = this.openMenu.bind(this);
          this.closeMenu = this.closeMenu.bind(this);
        //   this.setDropdownOpen=this.setDropdownOpen.bind(this);
        //   this.handleClick = this.handleClick.bind(this);
        };

        handleClick = (e, titleProps) => {
            const { index } = titleProps
            const { activeIndex } = this.state
            const newIndex = activeIndex === index ? -1 : index

            this.setState({ activeIndex: newIndex })
        }

        openMenu(){
            this.setState({
            menuStatus:"close",
            style:"menu active"
            });
        }

        closeMenu(){
            this.setState({
            menuStatus:"open",
            style:"menu"
            });
        }


        // handleClick() {
        //   switch(this.state.menuStatus)
        //   {
        //     case "open":
        //       this.setState({
        //         menuStatus:"close",
        //         style:"menu active"
        //       });
        //       break;
        //     case "close":
        //       this.setState({
        //         menuStatus:"open",
        //         style:"menu"
        //       });
        //       break;
        //   }        
        // }


        render() {
            // const { activeIndex } = this.state
            // const classes = useStyles();
            // const toggle = () => this.setDropdownOpen(prevState => !prevState);
          return (      
            <div>
                {/* <button onClick={this.handleClick}><div></div><div></div><div></div></button> */}
              <button onClick={this.openMenu}><div></div><div></div><div></div></button>

              <div className={this.state.style}>               

                    <button className = 'close' onClick={this.closeMenu}><Button close /></button>
                  <ul>
                  {navLinks.map(({ url, name }) => (
                    <li>
                      <a href={url}>{name}</a>
                      {/* { <a href={url}>Home</a>  */}
                    </li>

                  ))} 
                  <Accordion defaultActiveKey="0">
                    <Card>
                      <Accordion.Toggle as={Card.Header} eventKey="0">
                        Click me!
                      </Accordion.Toggle>
                      <Accordion.Collapse eventKey="0">
                        <Card.Body>Hello! I'm the body</Card.Body>
                      </Accordion.Collapse>
                    </Card>
                    <Card>
                      <Accordion.Toggle as={Card.Header} eventKey="1">
                        Click me!
                      </Accordion.Toggle>
                      <Accordion.Collapse eventKey="1">
                        <Card.Body>Hello! I'm another body</Card.Body>
                      </Accordion.Collapse>
                    </Card>
                  </Accordion>



              </ul>

            </div>


            </div>
          );
        }



}
// ReactDOM.render(
