import React, { Component } from 'react'
import '../css/Sidebar.scss';
// get our fontawesome imports
import { faHome } from "@fortawesome/fontawesome-free";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const navLinks = [
    { url: '/about-us', name: 'About Us' },
    { url: '/projects', name: 'Projects' },
    { url: '/services', name: 'Services' },
    { url: '/contact-us', name: 'Contact Us' },
  ];


class Sidebar extends Component {



        constructor(){
          super();
          this.state = {
            style:"menu",
            menuStatus:"open"
          };
          this.openMenu = this.openMenu.bind(this);
          this.closeMenu = this.closeMenu.bind(this);
        //   this.handleClick = this.handleClick.bind(this);
        };

        openMenu(){
            this.setState({
            menuStatus:"close",
            style:"menu active"
            });
        }

        closeMenu(){
            this.setState({
            menuStatus:"open",
            style:"menu"
            });
        }

        // handleClick() {
        //   switch(this.state.menuStatus)
        //   {
        //     case "open":
        //       this.setState({
        //         menuStatus:"close",
        //         style:"menu active"
        //       });
        //       break;
        //     case "close":
        //       this.setState({
        //         menuStatus:"open",
        //         style:"menu"
        //       });
        //       break;
        //   }        
        // }

        render() {
          return (      
            <div>
                {/* <button onClick={this.handleClick}><div></div><div></div><div></div></button> */}
              <button onClick={this.openMenu}><div></div><div></div><div></div></button>

              <div className={this.state.style}>               

                    <button className = 'close' onClick={this.closeMenu}><FontAwesomeIcon icon={faHome} /><i class="fas fa-times"></i></button>
                  <ul>
                  {navLinks.map(({ url, name }) => (
                    <li>
                      <a href={url}>{name}</a>
                      {/* <a href={url}>Home</a> */}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          );
        }



}
