import React, { Component } from 'react';
import {Container, ListGroup, ListGroupItem, Button, Row, Col} from 'reactstrap';
import {CSSTransition, TransitionGroup } from 'react-transition-group';
// import {v1 as uuid} from 'uuid';
import{connect} from 'react-redux';
import {getItems, deleteItem} from '../Actions/itemActions';
import PropTypes from 'prop-types';


class ShoppingList extends Component {

    // state = {
    //     items : [
    //         {id: uuid(), name : 'Eggs'},
    //         {id: uuid(), name : 'Milk'},
    //         {id: uuid(), name : 'Steak'},
    //         {id: uuid(), name : 'Water'}
    //     ]
    // }

    componentDidMount(){
        this.props.getItems();
    };

    onDeleteClick = id => {
        this.props.deleteItem(id);
    };

    render() {
        const { items } = this.props.item;
        return (
            <Container>
                {/* <Button
                color = "dark"
                style= {{marginBottom: "2rem"}}
                onClick = {() =>{
                    const name = prompt('Enter Item');
                    if(name){
                        this.setState(state => ({
                            items: [...state.items, {id: uuid(), name}]
                        }));
                    }
                }}
                >Add Item
                </Button> */}
                <div className="Header" style={{
                    fontSize:"30px",
                    fontWeight:"700",
                    fontFamily:"san-serif",
                    padding:"30px"
                }}>Hollywood's 100 Favorite Films</div>
                <ListGroup>
                    <TransitionGroup className="movie-list">
                        {items.map(({_id,name,cover,description}) => (
                            <CSSTransition key={_id} timeout={500} classNames="fade">
                                <ListGroupItem>
                                    <Row>
                                        <Button
                                            className="remove-btn"
                                            color="danger"
                                            size="sm"
                                            onClick={this.onDeleteClick.bind(this,_id)}
                                            // onClick={() => {
                                            //     this.setState(state => ({
                                            //         items: state.items.filter(item => item.id !==id)
                                            //     }));
                                            // }}
                                            >&times;
                                            Delete
                                        </Button>
                                        <Col sm="12" md={{size:12, offset:2}}>
                                        <h2>{name}</h2></Col>

                                    </Row>
                                    <Row>
                                        <Col sm="12" md={{ size: 10, offset: 1 }}>
                                            <img src={require(`../public/`+`${cover}`) } width="100%" height="500" />
                                            <figCaption>Fig.1 - Trulli, Puglia, Italy.</figCaption>
                                        </Col>



                                    </Row>
                                    <Row><Col sm="12" md={{ size: 10, offset: 1 }}>{description}</Col></Row>


                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        )
    }
}

ShoppingList.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    item: state.item
});

export default connect(mapStateToProps, {getItems, deleteItem})(ShoppingList);
